import { useEffect, useState } from "react";
import api from "../services/api";  // Use the updated api service with token handling
import Table from "../components/Table";  // Reusing your Table component

const Customers = () => {
  const columns = ["Name", "Email", "Shopify ID"];
  const [data, setData] = useState([]);

  useEffect(() => {
    async function fetchCustomers() {
      try {
        const res = await api.get("/customers");  // Using the API service to fetch customers
        console.log("Fetched customers:", res.data);  // Log the fetched customers
        const customers = res.data;

        const formatted = customers.map((customer) => [
          customer.name,
          customer.email,
          customer.shopify_customer_id,
        ]);

        setData(formatted);  // Set the state with formatted customer data
      } catch (err) {
        console.error("Failed to fetch customers:", err);  // Log any error
      }
    }

    fetchCustomers();  // Call the function to fetch customers on component mount
  }, []);

  return (
    <div className="container mt-4">
      <h1 className="h4 fw-bold mb-4">Customers</h1>

      <div className="table-responsive">
        <Table columns={columns} data={data} />  {/* Reusing your Table component */}
      </div>
    </div>
  );
};

export default Customers;
