import React, { useEffect, useState } from "react";
import api from "../services/api";
import Table from "../components/Table"; 
import { ShoppingCart } from "lucide-react"; // nice order icon

const Orders = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    async function fetchOrders() {
      try {
        const res = await api.get("/orders");
        setOrders(res.data);
      } catch (err) {
        console.error("Failed to fetch orders:", err);
      }
    }

    fetchOrders();
  }, []);

  const columns = ["Order ID", "Customer ID", "Total Price", "Date"];
  const data = orders.map((order) => [
    order.shopify_order_id,
    order.customer_id || "N/A",
    `₹${order.total_price}`,
    new Date(order.createdAt).toLocaleDateString("en-GB"),
  ]);

  return (
    <div className="min-h-screen bg-gradient-to-r from-pink-100 via-purple-100 to-indigo-100 p-6">
      <div className="bg-white shadow-xl rounded-2xl p-6 max-w-6xl mx-auto">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-2xl font-bold text-gray-800 flex items-center gap-2">
            <ShoppingCart className="text-indigo-600" size={28} /> Orders
          </h1>
          <span className="px-4 py-1 text-sm rounded-full bg-indigo-100 text-indigo-700 font-medium">
            {orders.length} Orders
          </span>
        </div>

        {/* Table */}
        <div className="overflow-x-auto">
          <Table columns={columns} data={data} />
        </div>
      </div>
    </div>
  );
};

export default Orders;
