import { BrowserRouter as Router, Routes, Route, Link, Navigate } from "react-router-dom";
import Dashboard from "./pages/Dashboard";
import Products from "./pages/Products";
import Orders from "./pages/Orders";
import Customers from "./pages/Customers";
import Login from "./pages/Login";  // Import Login page

export default function App() {
  // Check if the user is logged in from localStorage
  const isLoggedIn = localStorage.getItem("isLoggedIn");

  return (
    <Router>
      <div className="d-flex">
        {/* Sidebar */}
        <aside className="bg-dark text-white vh-100 p-4" style={{ width: '16rem' }}>
          <h1 className="h4 fw-bold mb-4">Shopify Analytics</h1>
          <nav className="nav flex-column">
            <Link to="/" className="nav-link text-white">Dashboard</Link>
            <Link to="/products" className="nav-link text-white">Products</Link>
            <Link to="/orders" className="nav-link text-white">Orders</Link>
            <Link to="/customers" className="nav-link text-white">Customers</Link>
          </nav>
        </aside>

        {/* Main Content */}
        <main className="flex-grow-1 bg-light p-4" style={{ minHeight: '100vh' }}>
          <Routes>
            {/* Login Route */}
            <Route path="/login" element={<Login />} />

            {/* Protected Routes */}
            <Route
              path="/"
              element={isLoggedIn ? <Dashboard /> : <Navigate to="/login" />}
            />
            <Route
              path="/customers"
              element={isLoggedIn ? <Customers /> : <Navigate to="/login" />}
            />
            <Route
              path="/products"
              element={isLoggedIn ? <Products /> : <Navigate to="/login" />}
            />
            <Route
              path="/orders"
              element={isLoggedIn ? <Orders /> : <Navigate to="/login" />}
            />
          </Routes>
        </main>
      </div>
    </Router>
  );
}
