import React, { useEffect, useState } from "react";
import axios from "axios";

const Customers = () => {
  const [customers, setCustomers] = useState([]);

  useEffect(() => {
    fetchCustomers();
  }, []);

 const fetchCustomers = async () => {
  try {
    const res = await axios.get("http://localhost:5000/api/customers");
    console.log("Fetched customers:", res.data);  // Log the fetched data here
    setCustomers(res.data);  // Make sure this updates the state correctly
  } catch (err) {
    console.error("Failed to fetch customers:", err);  // This should catch and log any errors
  }
};


  return (
    <div className="container mt-4">
      <h1 className="h4 fw-bold mb-4">Customers</h1>

      <div className="table-responsive">
        <table className="table table-bordered table-hover table-sm">
          <thead className="table-light">
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Shopify ID</th>
            </tr>
          </thead>
          <tbody>
            {customers.map((customer) => (
              <tr key={customer.id}>
                <td>{customer.name}</td>
                <td>{customer.email}</td>
                <td>{customer.shopify_customer_id}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Customers;
