import React, { useEffect, useState } from "react";
import api from "../services/api"; // assumes services/api.js is correct
import Table from "../components/Table"; // reusing your Table component

const Orders = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    async function fetchOrders() {
      try {
        const res = await api.get("/orders");
        setOrders(res.data);
      } catch (err) {
        console.error("Failed to fetch orders:", err);
      }
    }

    fetchOrders();
  }, []);

  const columns = ["Order ID", "Customer ID", "Total Price", "Date"];
  const data = orders.map((order) => [
    order.shopify_order_id,
    order.customer_id || "N/A",
    `₹${order.total_price}`,
    new Date(order.createdAt).toLocaleDateString("en-GB"),
  ]);

  return (
    <div className="p-4">
      <h1 className="mb-4 fw-bold">Orders</h1>
      <div className="table-responsive">
        <Table columns={columns} data={data} />
      </div>
    </div>
  );
};

export default Orders;
