import axios from "axios";

const API = axios.create({
  baseURL: "http://localhost:5000/api/auth",
});

// ✅ Register
export const registerUser = (data) =>
  API.post("/register", {
    ...data,
    tenant_id: 1, // ensure tenant_id is always sent
  });

// ✅ Login
export const loginUser = (data) =>
  API.post("/login", {
    ...data,
    tenant_id: 1, // ensure tenant_id is always sent
  });
